mapper.insert:
INSERT INTO board_study
(
	seq
	, mem_id
	, mem_name
	, board_subject 
	, board_content
	, reg_date
	, view_cnt
)
VALUES
(
	(SELECT NVL(MAX(seq), 0)+1 FROM board_study)
	, #{memId}
	, #{memName}
	, #{boardSubject}
	, #{boardContent} 
	, sysdate
	, 0
)

mapper.update:
	UPDATE board_study SET
		  mem_id 		=	#{memId }
		, mem_name 		=	#{memName }
		, board_subject =	#{boardSubject }
		, board_content =	#{boardContent }
		, upt_date 		=	sysdate
	WHERE seq=#{seq }
