mapper.select:
select rnum ,seq ,mem_id ,mem_name ,board_subject ,board_content ,reg_date ,upt_date ,view_cnt from ( select row_number() over(order by seq desc) rnum ,seq ,mem_id ,mem_name ,board_subject ,board_content ,reg_date ,upt_date ,view_cnt from board_study where 1=1
and MEM_NAME like '%${keyword}%'
and BOARD_SUBJECT like '%'||#{keyword}||'%'
and (BOARD_SUBJECT like '%'||#{keyword}||'%' or BOARD_CONTENT like '%'||#{keyword}||'%')
and reg_date between #{start_date} and #{end_date}
)where rnum between (#{pageNo}-1)*#{listSize}+1 and #{pageNo} * #{listSize}

mapper.insert:
INSERT INTO board_study
(
	seq
	, mem_id
	, mem_name
	, board_subject 
	, board_content
	, reg_date
	, view_cnt
)
VALUES
(
	(SELECT NVL(MAX(seq), 0)+1 FROM board_study)
	, #{memId}
	, #{memName}
	, #{boardSubject}
	, #{boardContent} 
	, sysdate
	, 0
)

mapper.update:
	UPDATE board_study SET
		  mem_id 		=	#{memId }
		, mem_name 		=	#{memName }
		, board_subject =	#{boardSubject }
		, board_content =	#{boardContent }
		, upt_date 		=	sysdate
	WHERE seq=#{seq }
